import React from 'react';
import BotCard from './BotCard';

const YourBotArmy = ({ army, onRemoveBot }) => {
  return (
    <div className="YourBotArmy">
      <h2>Your Bot Army</h2>
      <div className="BotsGrid">
        {army.map((bot) => (
          <BotCard key={bot.id} bot={bot} onRemoveBot={onRemoveBot} />
        ))}
      </div>
    </div>
  );
};

export default YourBotArmy;
