import React, { useState, useEffect } from "react";
import "./App.css";
import BotCollection from "./BotCollection";
import YourBotArmy from "./YourBotArmy";
import FilterBar from "./FilterBar";
import SortBar from "./SortBar";
import BotSpecs from './BotSpecs';


function App() {
  const [bots, setBots] = useState([]);
  const [selectedBots, setSelectedBots] = useState([]);
  const [showBotSpecs, setShowBotSpecs] = useState(false);
  const [selectedBot, setSelectedBot] = useState({});

  useEffect(() => {
    fetch("http://localhost:3000/bots")
      .then(res => res.json())
      .then(data => setBots(data));
  }, []);

  const handleSelectBot = bot => {
    setSelectedBot(bot);
    setShowBotSpecs(true);
  };

  const handleEnlistBot = bot => {
    const alreadySelected = selectedBots.find(b => b.id === bot.id);
    if (!alreadySelected) {
      setSelectedBots([...selectedBots, bot]);
    }
  };

  const handleReleaseBot = bot => {
    setSelectedBots(selectedBots.filter(b => b.id !== bot.id));
  };

  const handleDischargeBot = bot => {
    setSelectedBots(selectedBots.filter(b => b.id !== bot.id));
    setBots(bots.filter(b => b.id !== bot.id));
  };

  const handleSort = (sortType) => {
    setBots(bots.sort((a, b) => a[sortType] - b[sortType]));
  };


  return (
    <div className="App">
      <h1>Bot Army Manager</h1>
      {/* <FilterBar /> */}
      <SortBar onsort={handleSort}/>
      {showBotSpecs ? (
        <BotSpecs
          bot={selectedBot}
          onBack={() => setShowBotSpecs(false)}
          onEnlist={() => handleEnlistBot(selectedBot)}
        />
      ) : (
        <>
          <BotCollection bots={bots} onSelectBot={handleSelectBot} />
          <YourBotArmy bots={selectedBots} onRelease={handleReleaseBot} onDischarge={handleDischargeBot} />
        </>
      )}
    </div>
  );
}

export default App;
