import React from 'react';

const BotCard = ({ bot, onRemoveBot, onAddBot, onDischargeBot }) => {
  return (
    <div className="BotCard" style={{}}>
      <h3>{bot.name}</h3>
      <img src={bot.avatar_url} alt={bot.name} />
      <p>Class: {bot.bot_class}</p>
      <p>Health: {bot.health}</p>
      <p>Damage: {bot.damage}</p>
      <p>Armor: {bot.armor}</p>
      <p>Catchphrase: {bot.catchphrase}</p>
      <p>Created_at: {bot.created_at}</p>
      <p>Updated_at: {bot.updated_at}</p>
      <button onClick={() => onAddBot(bot.id)}>Add</button>
      <button onClick={() => onRemoveBot(bot)} style={{color:'purple', cursor:'pointer', marginLeft:'15px'}}>Remove</button>
      <button onClick={() => onDischargeBot(bot)} style={{color:'red', cursor:'pointer', marginLeft:'15px'}}>Delete</button>
    </div>
  );
};

export default BotCard;
