import React, {useState} from 'react';


const BotCard = ({ bot, onReleaseBot, onAddBot, onDischargeBot, bots }) => {

const [show, setShow] = useState(bots.map(() => true));

const handleClick = (id) => {
    setShow(show.map((val, i) => i !== id ? val : false));
  };

  return (
    <div className="BotCard" style={{border:'2px solid', marginBottom:'20px', marginTop:'15px', marginLeft:'20px'}}>
      <h3>{bot.name}</h3>
      <img src={bot.avatar_url} alt={bot.name} onClick={() => onAddBot(bot)} style={{cursor:'pointer'}}/>
      <p>Bot Id: {bot.id}</p>
      <p>Class: {bot.bot_class}</p>
      <p>Health: {bot.health}</p>
      <p>Damage: {bot.damage}</p>
      <p>Armor: {bot.armor}</p>
      <p>Catchphrase: {bot.catchphrase}</p>
      <p>Created_at: {bot.created_at}</p>
      <p>Updated_at: {bot.updated_at}</p>
      {bots.map((item, id) => (
        show[id] ? (
          <div key={item.id}>
            <p>{item.text}</p>
            <button onClick={() => handleClick(bot.id)}>Remove</button>
          </div>
        ) : null
      ))}
      {/* <button onClick={() => onReleaseBot(bot)} style={{color:'purple', cursor:'pointer', marginLeft:'40px'}}>Remove</button> */}
      <button onClick={() => onDischargeBot(bot)} style={{color:'red', cursor:'pointer', marginLeft:'150px', marginBottom:'2px'}}>Delete</button>
    </div>
  );
};

export default BotCard;
