import React, { useState, useEffect } from "react";
import "./App.css";
import BotCollection from "./BotCollection";
import SortBar from "./SortBar";
import FilterBar from "./FilterBar";

function App() {
  const [bots, setBots] = useState([]);
  const [selectedBots, setSelectedBots] = useState([]);
  const [sortOption, setSortOption] = useState("");
  const [filterOption, setFilterOption] = useState("");

  useEffect(() => {
    fetch("http://localhost:3000/bots")
      .then((res) => res.json())
      .then((data) => setBots(data))
      .catch((err) => console.error(err));
  }, []);

  // const handleAddBot = (bot) => {
  //   setSelectedBots([...selectedBots, bot]);
  // };

  function handleAddBot(bot) {
    setBots(bots.map((b) => (b.id === bot.id ? { ...b, army: true } : b)));
  }

  // const handleReleaseBot = (id) => {
  //   setSelectedBots(selectedBots.filter((bot) => bot.id !== id));
  // };

  const handleReleaseBot = (id) => {
    const updatedBots = selectedBots.map((bot) => {
      if (bot.id === id) {
        return { ...bot, isDisabled: false };
      }
      return bot;
    });
    setSelectedBots(updatedBots);
  };



  const handleDischargeBot = (id) => {
    const updatedBots = selectedBots.map((bot) => {
      if (bot.id === id) {
        return { ...bot, isDisabled: true };
      }
      return bot;
    });
    setSelectedBots(updatedBots);
  };

  const handleFilter = (filterBy) => {
    setFilterOption(filterBy);
    let filteredBots = [...bots];
    if (filterBy === "active") {
      filteredBots = filteredBots.filter((bot) => !bot.isDisabled);
    } else if (filterBy === "inactive") {
      filteredBots = filteredBots.filter((bot) => bot.isDisabled);
    }
    setSelectedBots(filteredBots);
  };

  const handleSort = (sortBy) => {
    setSortOption(sortBy);
    const sortedBots = [...selectedBots];
    if (sortBy === "health") {
      sortedBots.sort((a, b) => b.health - a.health);
    } else if (sortBy === "damage") {
      sortedBots.sort((a, b) => b.damage - a.damage);
    } else if (sortBy === "armor") {
      sortedBots.sort((a, b) => b.armor - a.armor);
    }
    setSelectedBots(sortedBots);
  };

  function refreshPage() {
    window.location.reload(false);
  }

  return (
    <div className="App">
      <h1 className="header">Your Bot Army</h1>
      <div className="content">
        <FilterBar onFilter={handleFilter} />
        <SortBar onSort={handleSort} style={{marginBottom: '30px'}} />
        <button onClick={refreshPage}>Click here to <b>Refilter and Resort</b></button>
        <BotCollection
          bots={bots}
          onAddBot={handleAddBot}
          onReleaseBot={handleReleaseBot}
          onDischargeBot={handleDischargeBot}
        />
      </div>
    </div>
  );
}

export default App;
